// HomePage.js

import React from 'react';
import { useNavigate } from 'react-router-dom';
import SearchBar from './SearchBar';
import CategoryWindow from './CategoryWindow';
import UploadBooks from './UploadBooks';

const HomePage = () => {
  const navigate = useNavigate();

  const handleCategoryButtonClick = () => {
    // Open Category Window logic
  };

  const handleCloseCategoryModal = () => {
    // Close Category Window logic
  };

  const handleUploadButtonClick = () => {
    // Navigate to the UploadBooks page
    navigate('/upload-books');
  };

  return (
    <div>
      <h1>Home Page</h1>

      {/* Search Bar Component */}
      <SearchBar />

      {/* Category Button */}
      <button onClick={handleCategoryButtonClick}>Open Category</button>

      {/* Category Modal */}
      {false && <CategoryWindow onClose={handleCloseCategoryModal} />}

      {/* Upload Button */}
      <button onClick={handleUploadButtonClick}>Upload Books</button>
    </div>
  );
};

export default HomePage;
