import React, { useState } from 'react';

const CategoryWindow = () => {
  const [categories, setCategories] = useState([
    'Fiction',
    'Non-Fiction',
    'Mystery',
    'Science Fiction',
    'Fantasy',
    // Add more categories as needed
  ]);

  const [filters, setFilters] = useState({
    author: false,
    genre: false,
    publicationYear: false,
  });

  const handleCheckboxChange = (filter) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [filter]: !prevFilters[filter],
    }));
  };

  return (
    <div>
      <h2>Categories</h2>
      <ul>
        {categories.map((category, index) => (
          <li key={index}>{category}</li>
        ))}
      </ul>

      <h3>Filter Options</h3>
      <label>
        <input
          type="checkbox"
          checked={filters.author}
          onChange={() => handleCheckboxChange('author')}
        />
        Author
      </label>

      <label>
        <input
          type="checkbox"
          checked={filters.genre}
          onChange={() => handleCheckboxChange('genre')}
        />
        Genre
      </label>

      <label>
        <input
          type="checkbox"
          checked={filters.publicationYear}
          onChange={() => handleCheckboxChange('publicationYear')}
        />
        Publication Year
      </label>
      <button className="close-button" onClick={onClose}>Close</button>
    </div>
  );
};

export default CategoryWindow;
