import React, { useState } from 'react';
import { SearchBar, CategoryWindow, UploadBooks } from './component'; 
import { Link } from 'react-router-dom'; 

const HomePage = () => {
  const [isCategoryWindowOpen, setCategoryWindowOpen] = useState(false);

  const handleCategoryButtonClick = () => {
    setCategoryWindowOpen(true);
  };

  const handleCloseCategoryModal = () => {
    setCategoryWindowOpen(false);
  };

  return (
    <div>
      <h1>Home Page</h1>

      {/* Search Bar Component */}
      <SearchBar />

      {/* Category Button */}
      <button onClick={handleCategoryButtonClick}>Category</button>

      {/* Category Modal */}
      {isCategoryWindowOpen && <CategoryWindow onClose={handleCloseCategoryModal} />}

      {/* Upload Button */}
      <Link to="/upload-book">
        <button>Tap to Upload Book</button>
      </Link>

      {/* Upload Books Component */}
      <UploadBooks />
    </div>
  );
};

export default HomePage;
