import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import SearchBar from './SearchBar';
import CategoryWindow from './CategoryWindow';
import UploadBooks from './UploadBooks';
import './ComponentStyles/HomePage.css'
import searchIcon from './ComponentStyles/Img/Search_'

const HomePage = () => {
  const navigate = useNavigate();
  const [isCategoryWindowOpen, setCategoryWindowOpen] = useState(false);

  const handleCategoryButtonClick = () => {
    setCategoryWindowOpen(true);
  };

  const handleCloseCategoryModal = () => {
    setCategoryWindowOpen(false);
  };

  const handleUploadButtonClick = () => {
    navigate('/upload-books');
  };

  return (
    <div className='HomePage'>
      <div className='Upper-Part'>
      <div className='TitleSection'>
      <h1 className='Title'>Bibliophilia</h1>
      <p className='subtitle'>A free online platform for bookworms </p>
      </div>
      </div>
      

      {/* Search Bar Component */}

      <div className='Search'>
  <div className='SearchBar'>
    <img
      className="SearchIcon"
      src= {searchIcon}
      alt="Search"
    />
    <input type="text" placeholder="Search for books, articles, documents..." />
  </div>
  <button className="SearchButton" onClick={() => alert("Search clicked")}>
    Search
  </button>
</div>


      {/* Category Button */}
      <button className="CategoryButton"onClick={handleCategoryButtonClick}>Open Category</button>

      {/* Category Modal */}
      {isCategoryWindowOpen && <CategoryWindow onClose={handleCloseCategoryModal} />}

      {/* Upload Button */}
      <button  className = "Upload Button" onClick={handleUploadButtonClick}>Upload Books</button>
    </div>
  );
};

export default HomePage;
