import React, { useState } from 'react';
import SearchBar from './SearchBar'; // Update the path based on your actual structure
import CategoryWindow from './CategoryWindow'; // Update the path based on your actual structure
import UploadBooks from './UploadBooks'; // Update the path based on your actual structure
import './Style'
const HomePage = () => {
  const [isCategoryWindowOpen, setCategoryWindowOpen] = useState(false);

  const handleCategoryButtonClick = () => {
    setCategoryWindowOpen(true);
  };

  const handleCloseCategoryModal = () => {
    setCategoryWindowOpen(false);
  };

  return (
    <div>
      <h1>Home Page</h1>

      {/* Search Bar Component */}
      <SearchBar />

      {/* Category Button */}
      <button onClick={handleCategoryButtonClick}>Open Category</button>

      {/* Category Modal */}
      {isCategoryWindowOpen && <CategoryWindow onClose={handleCloseCategoryModal} />}

      {/* Upload Button */}
      <UploadBooks />
    </div>
  );
};

export default HomePage;
